DROP TABLE IF EXISTS `weapon`;
CREATE TABLE `weapon` (
  `item_id` int NOT NULL,
  `name` varchar(70) default NULL,
  `bodypart` ENUM('RIGHT_HAND','TWO_HAND','LEFT_HAND','WOLF','HATCHLING','STRIDER') NOT NULL,
  `crystallizable` BOOL NOT NULL DEFAULT TRUE,
  `weight` decimal(4,0) default NULL,
  `soulshots` decimal(2,0) default NULL,
  `spiritshots` decimal(1,0) default NULL,
  `crystal_type` ENUM('NONE', 'D', 'C', 'B', 'A', 'S') NOT NULL,
  `rnd_dam` decimal(2,0) default NULL,
  `weaponType` ENUM ('SWORD','BLUNT','BIG_BLUNT','DAGGER','BOW','POLE','SHIELD','BIG_SWORD','ETC','FIST','DUAL_FIST','DUAL','PET_WEAPON','ROD') NOT NULL,
  `mp_consume` decimal(2,0) default NULL,
  `duration` decimal(3,0) default NULL,
  `price` decimal(11,0) default NULL,
  `crystal_count` int(4) default NULL,
  `sellable` BOOL NOT NULL DEFAULT TRUE,
  `dropable` BOOL NOT NULL DEFAULT TRUE,
  `destroyable` BOOL NOT NULL DEFAULT TRUE,
  `tradeable` BOOL NOT NULL DEFAULT TRUE,
  `item_skill_id` int NOT NULL default '0',
  `item_skill_lvl` int NOT NULL default '0',
  PRIMARY KEY  (`item_id`)
);

